# Calculate and fill list of dates
for i in range(start, end + 1):
    date = {}

    year = i // 12 + 2000
    month = i % 12

    if month == 0:
        month = 12
        year = year - 1

    date['month'] = month
    date['year'] = year

    dates.append(date)

